	(1, 'B&oacute; Hoa Hồng Beautiful You', 229000.000, 599000.000, 318, 'B&oacute; hoa hồng đỏ đơn giản được g&oacute;i rất trẻ trung v&agrave; thanh lịch l&agrave; một trong những mẫu Best Seller của ch&uacute;ng t&ocirc;i. L&agrave; lựa chọn ho&agrave;n hảo cho ng&agrave;y Valentine hoặc bất kỳ dịp đặc biệt n&agrave;o.', '&lt;p&gt;B&amp;oacute;&amp;nbsp;&lt;em&gt;Hoa Hồng Beautiful You &lt;/em&gt;gồm:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;48 b&amp;ocirc;ng Hoa Hồng đỏ&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;C&amp;aacute;c loại hoa &amp;amp; l&amp;aacute; kh&amp;aacute;c&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '2020-10-27 23:45:58', 1, 'Hoa_Hong1d.jpg', 1),
	(2, 'B&oacute; Hoa Fabulously Red', 319000.000, 619000.000, 201, 'B&oacute; hoa sang trọng v&agrave; thanh khiết với hoa Hồng đỏ được g&oacute;i xinh xắn. Đ&acirc;y sẽ l&agrave; m&oacute;n qu&agrave; bất ngờ v&agrave; ho&agrave;n hảo d&agrave;nh tặng người thương, gia đ&igrave;nh hoặc bạn b&egrave;. ', '&lt;p&gt;&lt;em&gt;B&amp;oacute; Hoa Fabulously Red gồm:&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;-&amp;nbsp;12 b&amp;ocirc;ng Hoa Hồng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- 06 b&amp;ocirc;ng Cẩm Chướng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- C&amp;aacute;c loại hoa v&amp;agrave; l&amp;aacute; kh&amp;aacute;c&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '2020-10-28 23:14:20', 1, 'Hoa_Cam_Chuong1.jpg', 9),
	(5, 'Giỏ Hoa Rose Land', 279000.000, 469000.000, 154, 'Giỏ hoa rực rỡ v&agrave; đầy sức sống với c&aacute;c loại Hoa Hồng nhiều m&agrave;u sắc. Đ&acirc;y sẽ l&agrave; m&oacute;n qu&agrave; bất ngờ v&agrave; ho&agrave;n hảo d&agrave;nh tặng người thương, gia đ&igrave;nh hoặc bạn b&egrave;.', '&lt;p&gt;&lt;em&gt;Giỏ Hoa Rose Land&lt;/em&gt;&amp;nbsp;&lt;em&gt;gồm:&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;-&amp;nbsp;10 b&amp;ocirc;ng Hoa Hồng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- 3 b&amp;ocirc;ng Cẩm Chướng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- C&amp;aacute;c loại hoa v&amp;agrave; l&amp;aacute; kh&amp;aacute;c&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '2020-10-30 21:54:26', 1, 'Hoa_Cam_Chuong5.jpg', 9),
	(7, 'Giỏ Hoa Spring Garden', 329000.000, 499000.000, 286, 'Giỏ hoa rực rỡ v&agrave; đầy sức sống với hoa Hồng, Đồng Tiền &amp; Cẩm Chướng tươi tắn. Đ&acirc;y sẽ l&agrave; m&oacute;n qu&agrave; bất ngờ v&agrave; ho&agrave;n hảo d&agrave;nh tặng người thương, gia đ&igrave;nh hoặc bạn b&egrave;.', '&lt;p&gt;&lt;em&gt;Giỏ Hoa Spring Garden&lt;/em&gt;&amp;nbsp;gồm:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;- 6 b&amp;ocirc;ng Hoa Hồng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- 7 b&amp;ocirc;ng Hoa Đồng Tiền&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- 4 b&amp;ocirc;ng Hoa Cẩm Chướng&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '2020-10-28 23:16:41', 1, 'SpringGarden_8a7fdabb-0ecd-4f76-82dc-5dd0583f2546_1024x10242x.jpg', 1),
	(11, 'B&oacute; Hoa Hồng Forever', 419000.000, 0.000, 363, 'B&oacute; hoa sang trọng với sự kết hợp của l&aacute; bạc với hoa Hồng trắng v&agrave; đỏ nồng n&agrave;n. Đ&acirc;y sẽ l&agrave; m&oacute;n qu&agrave; bất ngờ v&agrave; ho&agrave;n hảo d&agrave;nh tặng người thương, gia đ&igrave;nh hoặc bạn b&egrave;. ', '&lt;p&gt;&lt;em&gt;B&amp;oacute; Hoa Hồng Trắng &amp;amp; Đỏ Forever&amp;nbsp;&lt;/em&gt;bao gồm:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;-&amp;nbsp;12 b&amp;ocirc;ng Hoa Hồng&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;- C&amp;aacute;c loại hoa v&amp;agrave; l&amp;agrave; kh&amp;aacute;c&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '2020-10-28 23:21:49', 1, 'Hoa_Hong2.jpg', 9),
